mapper.select:
select
,status_name,u1.user_name,u2.user_name user_name1
 from (select  from patent

  patent_id=#{patentId,jdbcType=INTEGER}
   and patent_case_num like concat('%',#{patentCaseNum,jdbcType=VARCHAR},'%') 
   and patent_apply_num like concat('%',#{patentApplyNum,jdbcType=VARCHAR},'%') 
   and patent_apply_time=#{patentApplyTime,jdbcType=TIMESTAMP}
   and patent_status_id=#{patentStatusId,jdbcType=INTEGER}
   and patent_sign=#{patentSign,jdbcType=INTEGER}
   and patent_inventor like concat('%',#{patentInventor,jdbcType=VARCHAR},'%') 
   and patent_name like concat('%',#{patentName,jdbcType=VARCHAR},'%') 
   and patent_create_person=#{patentCreatePerson,jdbcType=INTEGER}
   and patent_writer=#{patentWriter,jdbcType=INTEGER}
   and ${specialCondition}

) p
 left join status s on p.patent_status_id=s.status_id
 left join user u1 on p. patent_create_person=u1.user_id
 left join user u2 on p.patent_writer=u2.user_id
